import { useEffect, useState } from 'react';
import './App.css';

// ‚úÖ Use environment variable or fallback to localhost (for local dev)
const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [quotes, setQuotes] = useState([]);
  const [quote, setQuote] = useState('');

  useEffect(() => {
    fetch(`${API_BASE}/api/quotes`)
      .then(res => res.json())
      .then(setQuotes)
      .catch(err => console.error('Error fetching quotes:', err));
  }, []);

  const submitQuote = (e) => {
    e.preventDefault();
    fetch(`${API_BASE}/api/quotes`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ quote })
    })
    .then(() => {
      setQuote('');
      return fetch(`${API_BASE}/api/quotes`).then(res => res.json());
    })
    .then(setQuotes)
    .catch(err => console.error('Error adding quote:', err));
  };

  const deleteQuote = (id) => {
    fetch(`${API_BASE}/api/quotes/${id}`, { method: 'DELETE' })
      .then(() => fetch(`${API_BASE}/api/quotes`).then(res => res.json()))
      .then(setQuotes)
      .catch(err => console.error('Error deleting quote:', err));
  };

  return (
    <div className="App">
      <h1>QuoteVault</h1>
      <form onSubmit={submitQuote}>
        <input 
          value={quote} 
          onChange={(e) => setQuote(e.target.value)} 
          placeholder="Your quote" 
        />
        <button type="submit">Add Quote</button>
      </form>
      <ul>
        {quotes.map(q => (
          <li key={q.id}>
            {q.quote}
            <button onClick={() => deleteQuote(q.id)}>üóëÔ∏è</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
